[ptrMM]=lastRED;    ptrMM++;    ptrMM %= maxperiod_siz;    samplesCounter++;    //    // if I've saved all the samples of a period, look to find    // max and min values and calculate R parameter    if(samplesCounter>=samples){      samplesCounter =0;      IRmax = 0; IRmin=1023; REDmax = 0; REDmin=1023;      for(int i=0;i<maxperiod_siz;i++) {        if( readsIRMM[i]> IRmax) IRmax = readsIRMM[i];        if( readsIRMM[i]>0 && readsIRMM[i]< IRmin ) IRmin = readsIRMM[i];        readsIRMM[i] =0;        if( readsREDMM[i]> REDmax) REDmax = readsREDMM[i];        if( readsREDMM[i]>0 && readsREDMM[i]< REDmin ) REDmin = readsREDMM[i];        readsREDMM[i] =0;      }      R =  ( (REDmax-REDmin) / REDmin) / ( (IRmax-IRmin) / IRmin ) ;    }            // check that the finger is placed inside    // the sensor. If the finger is missing     // RED curve is under the IR.    //    if (lastRED < lastIR) {      if(finger_status==true) {        finger_status = false;       // lcd.clear();       // lcd.setCursor(0,0);       // lcd.print(\\"No finger?\\");         //Serial.println(\\"No finger?\\");             }    } else {      if(finger_status==false) {       // lcd.clear();        finger_status = true;            //lcd.setCursor(10,0);            //lcd.print(\\"c=\\"); //Serial.println(\\"c\\");            //lcd.setCursor(0,0);            //lcd.print(\\"bpm\\");           // lcd.setCursor(0,1);           // lcd.print(\\"SpO\\"); lcd.write(1);  //2                       // lcd.setCursor(10,1);          //  lcd.print(\\"R=\\");              }    }    float avR = 0;    avBPM=0;       if (finger_status==true){                  // lastIR holds the average of the values in the array       // check for a rising curve (= a heart beat)       if (lastIR > beforeIR)       {          rise_count++;  // count the number of samples that are rising         if (!rising && rise_count > rise_threshold)         {          // lcd.setCursor(3,0);           // lcd.write( 0 );       // <3            // Ok, we have detected a rising curve, which implies a heartbeat.            // Record the time since last beat, keep track of the 10 previous            // peaks to get an average value.            // The rising flag prevents us from detecting the same rise             // more than once.             rising = true;            measuresR[m] = R;            measuresPeriods[m] = millis() - last_beat;            last_beat = millis();            int period = 0;            for(int i =0; i<measures; i++) period += measuresPeriods[i];            // calculate average period and number of samples            // to store to find min and max values            period = period / measures;            samples = period / (2*T);                           int avPeriod = 0;            int c = 0;            // c stores the number of good measures (not floating more than 10%),            // in the last 10 peaks            for(int i =1; i<measures; i++) {              if ( (measuresPeriods[i] <  measuresPeriods[i-1] * 1.1)  &&                      (measuresPeriods[i] >  measuresPeriods[i-1] / 1.1)  ) {                  c++;                  avPeriod += measuresPeriods[i];                  avR += measuresR[i];              }            }                          m++;            m %= measures;                                   // lcd.setCursor(12,0);           // lcd.print(String(c)+\\"  \\"); //Serial.println(String(c)+\\"  \\");            // bpm and R shown are calculated as the            // average of at least 5 good peaks            avBPM = 60000 / ( avPeriod / c) ;            avR = avR / c ;            // if there are at last 5 measures             //lcd.setCursor(12,1);            if(c==0) /*lcd.print(\\"    \\");*/ Serial.println(\\"  \\");            else /*lcd.print(String(avR) + \\" \\");*/ Serial.println(\\" \\");                        // if there are at least 5 good measures...            if(c > 4) {              //              // SATURTION IS A FUNCTION OF R (calibration)           