import{_ as t,c as a,b as i,o}from"./chunks/framework.B1z0IdBH.js";const u=JSON.parse('{"title":"Autoflow","description":"","frontmatter":{},"headers":[{"level":2,"title":"UI 界面","slug":"ui-界面","link":"#ui-界面","children":[]},{"level":2,"title":"集成 Deepseek API","slug":"集成-deepseek-api","link":"#集成-deepseek-api","children":[]}],"relativePath":"guide/autoflow/README_cn.md","filePath":"guide/autoflow/README_cn.md"}'),r={name:"guide/autoflow/README_cn.md"};function s(l,e,c,p,d,n){return o(),a("div",null,e[0]||(e[0]=[i('<h1 id="autoflow" tabindex="-1">Autoflow <a class="header-anchor" href="#autoflow" aria-label="Permalink to &quot;Autoflow&quot;">​</a></h1><p><a href="https://trendshift.io/repositories/12294" target="_blank"><img src="https://trendshift.io/api/badge/repositories/12294" alt="pingcap%2Fautoflow | Trendshift" style="width:250px;height:55px;" width="250" height="55"></a></p><p><a href="https://github.com/pingcap/autoflow" target="_blank" rel="noreferrer">AutoFlow</a> 是一个基于 GraphRAG（基于图的检索增强生成）的开源知识库工具，构建于 <a href="https://www.pingcap.com/ai?utm_source=tidb.ai&amp;utm_medium=community" target="_blank" rel="noreferrer">TiDB</a> Vector、LlamaIndex 和 DSPy 之上。它提供类似 Perplexity 的搜索界面，并允许通过嵌入简单的 JavaScript 代码片段，将 AutoFlow 的对话式搜索窗口轻松集成到您的网站中。</p><h2 id="ui-界面" tabindex="-1">UI 界面 <a class="header-anchor" href="#ui-界面" aria-label="Permalink to &quot;UI 界面&quot;">​</a></h2><ol><li><p><strong>Perplexity 风格的对话式搜索页面</strong>：我们的平台配备了高级内置网站爬虫，旨在提升您的浏览体验。该爬虫能够轻松抓取官方网站和文档站点，通过 sitemap 抓取，实现全面覆盖和高效搜索。</p><p><img src="https://github.com/user-attachments/assets/50a4e5ce-8b93-446a-8ce7-11ed7844bd1e" alt="Image"></p></li><li><p><strong>可嵌入的 JavaScript 代码片段</strong>：通过复制并嵌入一段简单的 JavaScript 代码，即可轻松将我们的对话式搜索窗口集成到您的网站中。此小部件通常放置在网站右下角，可即时回答与产品相关的查询。</p><p><img src="https://github.com/user-attachments/assets/f0dc82db-c14d-4863-a242-c7da3a719568" alt="Image"></p></li></ol><h2 id="集成-deepseek-api" tabindex="-1">集成 Deepseek API <a class="header-anchor" href="#集成-deepseek-api" aria-label="Permalink to &quot;集成 Deepseek API&quot;">​</a></h2><ul><li>点击 <code>Models</code> 选项卡，然后进入 <code>LLMs</code> 以进入 LLM 模型管理页面。</li><li>点击 <code>Create</code> 按钮创建一个新的 LLM 模型。</li><li>按照下方示例输入数据，然后点击 <code>Create LLM</code> 按钮。</li></ul><p><img src="https://github.com/user-attachments/assets/875cac18-707b-465f-ac62-89ddb416f94d" alt="Image"></p>',8)]))}const m=t(r,[["render",s]]);export{u as __pageData,m as default};
