import{_ as l,a as i,b as n,c as t}from"./chunks/roocline-00.CFT_m2r4.js";import{_ as a,c as o,b as r,o as s}from"./chunks/framework.B1z0IdBH.js";const _=JSON.parse('{"title":"Cline","description":"","frontmatter":{},"headers":[{"level":2,"title":"安装","slug":"安装","link":"#安装","children":[]},{"level":2,"title":"Cline 的主要特点","slug":"cline-的主要特点","link":"#cline-的主要特点","children":[]},{"level":2,"title":"用户界面","slug":"用户界面","link":"#用户界面","children":[]},{"level":2,"title":"与 DeepSeek API 集成","slug":"与-deepseek-api-集成","link":"#与-deepseek-api-集成","children":[]},{"level":2,"title":"最佳实践","slug":"最佳实践","link":"#最佳实践","children":[]},{"level":2,"title":"Roo Cline","slug":"roo-cline","link":"#roo-cline","children":[]}],"relativePath":"guide/cline/README_cn.md","filePath":"guide/cline/README_cn.md"}'),p={name:"guide/cline/README_cn.md"};function c(h,e,d,g,u,m){return s(),o("div",null,e[0]||(e[0]=[r('<h1 id="cline" tabindex="-1"><a href="https://github.com/cline/cline" target="_blank" rel="noreferrer">Cline</a> <a class="header-anchor" href="#cline" aria-label="Permalink to &quot;[Cline](https://github.com/cline/cline)&quot;">​</a></h1><p>Cline 是一款能够使用您的 CLI 和编辑器的 AI 助手。</p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p><img src="'+l+'" alt="image"></p><h2 id="cline-的主要特点" tabindex="-1">Cline 的主要特点 <a class="header-anchor" href="#cline-的主要特点" aria-label="Permalink to &quot;Cline 的主要特点&quot;">​</a></h2><ol><li><p><strong>基于 Claude 3.5 Sonnet 的智能编码</strong>：Cline 利用先进的 AI 技术，逐步处理复杂的软件开发任务，超越简单的代码补全或技术支持。</p></li><li><p><strong>文件与项目管理</strong>：Cline 可以创建、编辑和浏览大型项目中的文件，实现无缝的代码库导航和修改。</p></li><li><p><strong>终端集成</strong>：在用户授权的情况下，Cline 可以执行终端命令并监控其输出，从而动态响应开发服务器错误等问题。</p></li><li><p><strong>浏览器自动化</strong>：对于 Web 开发，Cline 可以在无头浏览器中启动网站，与页面交互（点击、输入、滚动），并捕获截图和控制台日志，以调试运行时错误和视觉问题。</p></li><li><p><strong>人机交互安全保障</strong>：每次文件更改和终端命令都需要通过 GUI 获得用户批准，确保在探索智能 AI 功能时提供一个安全可控的环境。</p></li><li><p><strong>上下文感知辅助</strong>：Cline 分析文件结构、源代码 AST 并运行正则表达式搜索，以理解现有项目，确保在不超出上下文窗口的情况下提供高效且相关的帮助。</p></li><li><p><strong>主动错误处理</strong>：Cline 监控 linter/编译器错误，并在文件编辑过程中主动修复诸如缺少导入和语法错误等问题。</p></li><li><p><strong>模型上下文协议 (MCP)</strong>：Cline 可以通过 MCP 创建新工具来扩展其功能，从而持续适应用户需求。</p></li><li><p><strong>轻松完成任务</strong>：任务完成后，Cline 会以简单的终端命令（例如 <code>open -a &quot;Google Chrome&quot; index.html</code>）呈现结果，用户只需点击一下即可执行。</p></li></ol><h2 id="用户界面" tabindex="-1">用户界面 <a class="header-anchor" href="#用户界面" aria-label="Permalink to &quot;用户界面&quot;">​</a></h2><p><img src="'+i+'" alt="image"></p><h2 id="与-deepseek-api-集成" tabindex="-1">与 DeepSeek API 集成 <a class="header-anchor" href="#与-deepseek-api-集成" aria-label="Permalink to &quot;与 DeepSeek API 集成&quot;">​</a></h2><p><img src="'+n+'" alt="image"></p><h2 id="最佳实践" tabindex="-1">最佳实践 <a class="header-anchor" href="#最佳实践" aria-label="Permalink to &quot;最佳实践&quot;">​</a></h2><ol><li><p><strong>关闭 Cline 的 Auto Approve 功能</strong>：建议在使用 DeepSeek 时，关闭 Cline 的 Auto Approve 功能，以降低资源消耗，降低多请求因并发带来的卡顿风险。</p></li><li><p><strong>使用 Cline 改版</strong>：例如 Roo-Cline 或 ALine。这些改版携带的上下文相对短，处理请求时表现更为流畅，且缓存命中率更高，可以帮助节省 API 调用成本。</p></li><li><p><strong>慎用 DeepSeek 处理大型工程</strong>：对于大型代码工程，不建议使用 DeepSeek + Cline。大型工程通常会携带大量代码上下文，会导致 API 请求处理速度明显变慢。</p></li><li><p><strong>更换其他 LLM 提供商</strong>：建议您在使用 Cline 时，更换其他 LLM 提供商，以获得更流畅的体验。</p></li></ol><h2 id="roo-cline" tabindex="-1">Roo Cline <a class="header-anchor" href="#roo-cline" aria-label="Permalink to &quot;Roo Cline&quot;">​</a></h2><p><img src="'+t+'" alt="image"></p>',14)]))}const b=a(p,[["render",c]]);export{_ as __pageData,b as default};
