import{_ as a,c as n,b as e,o}from"./chunks/framework.B1z0IdBH.js";const g=JSON.parse('{"title":"HuixiangDou","description":"","frontmatter":{},"headers":[],"relativePath":"guide/huixiangdou/README.md","filePath":"guide/huixiangdou/README.md"}'),i={name:"guide/huixiangdou/README.md"};function t(l,s,p,r,u,c){return o(),n("div",null,s[0]||(s[0]=[e(`<img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/huixiangdou.jpg" width="64" height="auto"><h1 id="huixiangdou" tabindex="-1"><a href="https://github.com/InternLM/HuixiangDou" target="_blank" rel="noreferrer">HuixiangDou</a> <a class="header-anchor" href="#huixiangdou" aria-label="Permalink to &quot;[HuixiangDou](https://github.com/InternLM/HuixiangDou)&quot;">​</a></h1><p>&quot;HuixiangDou&quot; is a domain knowledge assistant based on LLM. Features:</p><ol><li>Handles complex scenarios such as group chat and handle answer user questions without flooding messages.</li><li>Proposes a solution for technical issues through an algorithm pipeline.</li><li>Low cost, see <a href="https://arxiv.org/abs/2401.08772" target="_blank" rel="noreferrer">arxiv2401.08772</a></li></ol><h1 id="demonstration" tabindex="-1">Demonstration <a class="header-anchor" href="#demonstration" aria-label="Permalink to &quot;Demonstration&quot;">​</a></h1><p>HuixiangDou is a <strong>assistant</strong></p><ul><li>Says it doesn&#39;t know in response to halluciations</li><li>Personal Wechat supported</li><li>Feishu supported</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/demo0.jpg" width="400"><p>HuixiangDou is a <strong>group</strong> assistant</p><ul><li>Ignores idle talk</li><li>Only interacts with those who need help</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/inside-mmpose.jpg" width="400"><p>HuixiangDou is a <strong>domain knowledge</strong> group assistant</p><ul><li>Relies on an algorithm pipeline + LLM instead of training</li><li>Has faced thousands of users, and has been &quot;teased&quot; over 20,000 times in half a year</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/inside-middleware.png" width="400"><h1 id="configuring-deepseek-api" tabindex="-1">Configuring deepseek API <a class="header-anchor" href="#configuring-deepseek-api" aria-label="Permalink to &quot;Configuring deepseek API&quot;">​</a></h1><p><strong>STEP1.</strong> Apply for a token at deepseek open platform</p><p><strong>STEP2.</strong> Follow the HuixiangDou Advanced Configuration and fill in the config.ini with your token</p><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># config.ini</span></span>
<span class="line"><span style="color:#B392F0;">[llm]</span></span>
<span class="line"><span style="color:#F97583;">enable_local</span><span style="color:#E1E4E8;"> = 0</span></span>
<span class="line"><span style="color:#F97583;">enable_remote</span><span style="color:#E1E4E8;"> = 1</span></span>
<span class="line"><span style="color:#E1E4E8;">..</span></span>
<span class="line"><span style="color:#B392F0;">[llm.server]</span></span>
<span class="line"><span style="color:#E1E4E8;">..</span></span>
<span class="line"><span style="color:#F97583;">remote_type</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;deepseek&quot;</span></span>
<span class="line"><span style="color:#F97583;">remote_api_key</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;YOUR-API-KEY&quot;</span></span>
<span class="line"><span style="color:#F97583;">remote_llm_max_text_length</span><span style="color:#E1E4E8;"> = 16000</span></span>
<span class="line"><span style="color:#F97583;">remote_llm_model</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;deepseek-chat&quot;</span></span></code></pre></div><p><strong>STEP3.</strong> Run</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#B392F0;">python3</span><span style="color:#79B8FF;"> -m</span><span style="color:#9ECBFF;"> huixiangdou.main</span><span style="color:#79B8FF;"> --standalone</span></span></code></pre></div>`,20)]))}const h=a(i,[["render",t]]);export{g as __pageData,h as default};
