import{_ as s,c as a,b as n,o as l}from"./chunks/framework.B1z0IdBH.js";const g=JSON.parse('{"title":"茴香豆","description":"","frontmatter":{},"headers":[{"level":2,"title":"效果展示","slug":"效果展示","link":"#效果展示","children":[]},{"level":2,"title":"配置 deepseek API","slug":"配置-deepseek-api","link":"#配置-deepseek-api","children":[]}],"relativePath":"guide/huixiangdou/README_cn.md","filePath":"guide/huixiangdou/README_cn.md"}'),o={name:"guide/huixiangdou/README_cn.md"};function t(i,e,p,r,c,d){return l(),a("div",null,e[0]||(e[0]=[n(`<img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/huixiangdou.jpg" width="64" height="auto"><h1 id="茴香豆" tabindex="-1"><a href="https://github.com/InternLM/HuixiangDou" target="_blank" rel="noreferrer">茴香豆</a> <a class="header-anchor" href="#茴香豆" aria-label="Permalink to &quot;[茴香豆](https://github.com/InternLM/HuixiangDou)&quot;">​</a></h1><p>“茴香豆”是一个基于 LLM 的领域知识助手。特点：</p><ol><li>应对群聊这类复杂场景，解答用户问题的同时，不会消息泛滥</li><li>提出一套解答技术问题的算法 pipeline</li><li>部署成本低，见<a href="https://arxiv.org/abs/2401.08772" target="_blank" rel="noreferrer">技术报告 arxiv2401.08772</a></li></ol><h2 id="效果展示" tabindex="-1">效果展示 <a class="header-anchor" href="#效果展示" aria-label="Permalink to &quot;效果展示&quot;">​</a></h2><ol><li><p>它是个<strong>机器人</strong></p><ul><li>面对幻觉会说不知道</li><li>支持微信</li><li>支持飞书</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/demo0.jpg" width="400"></li><li><p>它是个<strong>群组</strong>机器人</p><ul><li>不会搭理闲聊</li><li>只找需要帮助的人</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/inside-mmpose.jpg" width="400"></li><li><p>它是个<strong>领域知识</strong>群组机器人</p><ul><li>不依赖训练，依靠算法 pipeline + LLM</li><li>已经面对数千用户，半年被“调戏”了 2w 次以上</li></ul><img src="https://github.com/InternLM/HuixiangDou/releases/download/v0.1.0rc1/inside-middleware.png" width="400"></li></ol><h2 id="配置-deepseek-api" tabindex="-1">配置 deepseek API <a class="header-anchor" href="#配置-deepseek-api" aria-label="Permalink to &quot;配置 deepseek API&quot;">​</a></h2><p><strong>STEP1.</strong> 打开 <a href="https://platform.deepseek.com" target="_blank" rel="noreferrer">deepseek 开放平台</a> 申请 token</p><p><strong>STEP2.</strong> 参照 <a href="https://github.com/InternLM/HuixiangDou?tab=readme-ov-file#step4-advanced-version-optional" target="_blank" rel="noreferrer">HuixiangDou 高级配置</a>，把 token 填入 <code>config.ini</code></p><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># config.ini</span></span>
<span class="line"><span style="color:#B392F0;">[llm]</span></span>
<span class="line"><span style="color:#F97583;">enable_local</span><span style="color:#E1E4E8;"> = 0</span></span>
<span class="line"><span style="color:#F97583;">enable_remote</span><span style="color:#E1E4E8;"> = 1</span></span>
<span class="line"><span style="color:#E1E4E8;">..</span></span>
<span class="line"><span style="color:#B392F0;">[llm.server]</span></span>
<span class="line"><span style="color:#E1E4E8;">..</span></span>
<span class="line"><span style="color:#F97583;">remote_type</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;deepseek&quot;</span></span>
<span class="line"><span style="color:#F97583;">remote_api_key</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;YOUR-API-KEY&quot;</span></span>
<span class="line"><span style="color:#F97583;">remote_llm_max_text_length</span><span style="color:#E1E4E8;"> = 16000</span></span>
<span class="line"><span style="color:#F97583;">remote_llm_model</span><span style="color:#E1E4E8;"> = </span><span style="color:#9ECBFF;">&quot;deepseek-chat&quot;</span></span></code></pre></div><p><strong>STEP3.</strong> 运行</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#B392F0;">python3</span><span style="color:#79B8FF;"> -m</span><span style="color:#9ECBFF;"> huixiangdou.main</span><span style="color:#79B8FF;"> --standalone</span></span></code></pre></div>`,12)]))}const h=s(o,[["render",t]]);export{g as __pageData,h as default};
